INSERT INTO layers VALUES (813, 'Substrate_datasupt', 'Data levels supporting soil property interpretations (index)', 'Environmental', 'CSIRO Ecosystem Sciences', '/mnt/transfer/williams-substrate/ARC/datasupt.tif', NULL, -43.80000, 112.90000, -9.00000, 153.64000, 'The interpretations of basic soil properties in McKenzie et al. (2000) are derived from the CSIRO National Soils Database where possible and the availability of this information is provided in index form where 1 = >20 profiles + ancillary information, 2 = 5-20 profiles + ancillary information and 3 = interpolated from other PPF interpretations. This index reflects the relative data support for all subsequent interpretations. These indices were averaged using the weights in Table 1, 0.25 was then added to the average and the resulting value was rounded. This process provided a little more weight to the more uncertain PPFs in the soil landscape. Derived from Western and McKenzie (2004)', true, 'Substrate data reliability', 'http://spatial.ala.org.au/geoserver/gwc/service/wms?service=WMS&version=1.1.0&request=GetMap&layers=ALA:Substrate_datasupt&format=image/png&styles=', NULL, '0', '8.422', 'dimensionless', NULL, NULL, 'Substrate', NULL, '813', '2010-07', NULL, 'eng', NULL, NULL, 1, NULL, 'Permission required to re-distribute derivative works. Please contact Dr. Kristen Williams - kristen.williams@csiro.au', 'http://www.csiro.au/org/ces.html');
