INSERT INTO layers VALUES (835, 'Substrate_geolrngeage', 'Geological age range (M years)', 'Environmental', 'CSIRO Ecosystem Sciences', '/mnt/transfer/williams-substrate/ARC/geolrngeage.tif', NULL, -43.80000, 112.90000, -9.00000, 153.64000, 'Range in geological ages (millions of years before present) derived from the 1:1 million surface geology of Australia (Raymond 2009) by combining the descriptive geological age fields with the timescales database (Laurie et al. 2008) to assign numerical ages to each map unit. The range in age values is derived from the maximum and minimum of four fields (MIN_TOPAGE, MAX_TOPAGE, MIN_BOTAGE, MAX_BOTAGE).  Rock voids without data including lakes were filled by neighbourhood statistics.', true, 'Lithology - age range', 'http://spatial.ala.org.au/geoserver/gwc/service/wms?service=WMS&version=1.1.0&request=GetMap&layers=ALA:Substrate_geolrngeage&format=image/png&styles=', NULL, '0.022', '118.641', 'my', NULL, NULL, 'Substrate', 'Age', '835', '2010-07', NULL, 'eng', NULL, NULL, 1, NULL, 'Permission required to re-distribute derivative works. Please contact Dr. Kristen Williams - kristen.williams@csiro.au', 'http://www.csiro.au/org/ces.html');
