INSERT INTO layers VALUES (812, 'Substrate_solpawhc', 'Plant-available soil water holding capacity (mm)', 'Environmental', 'CSIRO Ecosystem Sciences', '/mnt/transfer/williams-substrate/ARC/solpawhc.tif', NULL, -43.80000, 112.90000, -9.00000, 153.64000, 'The weighted average of the solum plant available water holding capacity (PAWHC) values provided by McKenzie et al. (2000). Solum PAWHC is defined as the sum of the PAWHC for the A and B horizons, which are calculated as (field capacity â€“ wilting point)*horizon depth. Note that for PPFs without a B horizon the solum PAWHC was taken as equal to the A horizon PAWHC. Units are mm. Derived from Western and McKenzie (2004).', true, 'Water holding capacity - plant-available ', 'http://spatial.ala.org.au/geoserver/gwc/service/wms?service=WMS&version=1.1.0&request=GetMap&layers=ALA:Substrate_solpawhc&format=image/png&styles=', NULL, '0', '2.255', 'mm', NULL, NULL, 'Substrate', 'Moisture', '812', '2010-07', NULL, 'eng', NULL, NULL, 1, NULL, 'Permission required to re-distribute derivative works. Please contact Dr. Kristen Williams - kristen.williams@csiro.au', 'http://www.csiro.au/org/ces.html');
INSERT INTO layers VALUES (717, 'wpot_mean', 'Mean annual soil water potential (MPa)', 'Environmental', 'CSIRO Ecosystem Sciences', '/mnt/transfer/williams/arc/wpot_mean.tif', NULL, -43.80000, 112.90000, -9.00000, 153.64000, 'The monthly soil volumetric water content in units of pressure potential between field capacity (0 bars) and wilting point (-15 bars / -1.5 MPa), derived from a model of water balance.  ', true, 'Water potential - annual mean', 'http://spatial.ala.org.au/geoserver/gwc/service/wms?service=WMS&version=1.1.0&request=GetMap&layers=ALA:wpot_mean&format=image/png&styles=', NULL, '-1.5', '0', '', '', NULL, 'Substrate', 'Moisture', '717', '2010-07', NULL, 'eng', NULL, NULL, 1, NULL, 'Permission required to re-distribute derivative works. Please contact Dr. Kristen Williams - kristen.williams@csiro.au', 'http://www.csiro.au/org/ces.html');
INSERT INTO layers VALUES (708, 'pwat_max', 'Maximum month soil water stress index ', 'Environmental', 'CSIRO Ecosystem Sciences', '/mnt/transfer/williams/arc/pwat_max.tif', NULL, -43.80000, 112.90000, -9.00000, 153.64000, 'The monthly water stress is the percentage of actual to potential evapotranspiration.  Adapted from the index by (Hackett 1988).  Actual evapotranspiration is an output of a water balance model, and potential evapotranspiration is the pan evaporation (free-water surface) adjusted by the coefficient of potential evapotranspiration (PETCF). A constant PETCF value of 0.9 was used for all months which assumes constant leaf area index.  ', true, 'Water stress index - month max', 'http://spatial.ala.org.au/geoserver/gwc/service/wms?service=WMS&version=1.1.0&request=GetMap&layers=ALA:pwat_max&format=image/png&styles=', NULL, '0', '81', '', '', NULL, 'Substrate', 'Moisture', '708', '2010-07', NULL, 'eng', NULL, NULL, 1, NULL, 'Permission required to re-distribute derivative works. Please contact Dr. Kristen Williams - kristen.williams@csiro.au', 'http://www.csiro.au/org/ces.html');
