INSERT INTO layers VALUES (832, 'Substrate_gravity', 'Bouguer gravity anomalies (acceleration, Gal)', 'Environmental', 'CSIRO Ecosystem Sciences', '/mnt/transfer/williams-substrate/ARC/gravity.tif', NULL, -43.80000, 112.90000, -9.00000, 153.64000, 'The 2009 edition of the onshore gravity grid of Australia is a compilation of over 1.4 Million gravity stations using Bouguer gravity anomalies in galileo units of acceleration (Gal). The original resolution data (Geoscience Australia 2009) were bilinear-resampled from 400m resolution to 1km. Small areas without data were filled by neighbourhood statistics.', true, 'Bouger gravity anamalies', 'http://spatial.ala.org.au/geoserver/gwc/service/wms?service=WMS&version=1.1.0&request=GetMap&layers=ALA:Substrate_gravity&format=image/png&styles=', NULL, '0', '3', '', NULL, NULL, 'Substrate', 'Physics', '832', '2010-07', NULL, 'eng', NULL, NULL, 1, NULL, 'Permission required to re-distribute derivative works. Please contact Dr. Kristen Williams - kristen.williams@csiro.au', 'http://www.csiro.au/org/ces.html');
INSERT INTO layers VALUES (814, 'Substrate_clay', 'Solum average median clay content (%)', 'Environmental', 'CSIRO Ecosystem Sciences', '/mnt/transfer/williams-substrate/ARC/clay.tif', NULL, -43.80000, 112.90000, -9.00000, 153.64000, 'Estimated median percentage clay content of estimated soil texture.  The estimated clay contents for 6 texture groups by McDonald et al. (1990) were used as a guide: interpreted values were increased or decreased depending on the type of soil within a group. Derived from McKenzie et al. (2000).  The solum average clay content is the A and B horizon estimates weighted by the depth of each horizon. ', true, 'Clay %', 'http://spatial.ala.org.au/geoserver/gwc/service/wms?service=WMS&version=1.1.0&request=GetMap&layers=ALA:Substrate_clay&format=image/png&styles=', NULL, '0', '3.591', '%', NULL, NULL, 'Substrate', 'Physics', '814', '2010-07', NULL, 'eng', NULL, NULL, 1, NULL, 'Permission required to re-distribute derivative works. Please contact Dr. Kristen Williams - kristen.williams@csiro.au', 'http://www.csiro.au/org/ces.html');
