INSERT INTO layers VALUES (21, 'imcra4_pb', 'Integrated Marine and Coastal Regionalisation of Australia', 'Contextual', 'ERIN', '/mnt/ala/data/contextual/ibra ??', '93.412,-58.449,171.801,-8.473', -58.44900, 93.41200, -8.47300, 171.80100, 'The Integrated Marine and Coastal Regionalisation of Australia (IMCRA v4.0) is a spatial framework for classifying Australias marine environment into bioregions that make sense ecologically and are at a scale useful for regional planning. These bioregions are the basis for the development of a National Representative System of Marine Protected Areas (NRSMPA).', true, 'IMCRA Regions', 'http://spatial.ala.org.au/geoserver/gwc/service/wms?service=WMS&version=1.1.0&request=GetMap&layers=ALA:imcra4_pb&format=image/png&styles=', NULL, '', '', 'region', '', 'http://www.environment.gov.au/coasts/mbp/imcra/index.html', 'Biodiversity', 'Region', '21', '2010-07', '2006', 'eng', NULL, 'custodian', 2, 'http://www.osdm.gov.au/OSDM/Policies+and+Guidelines/Spatial+Data+Access+and+Pricing/Sample+Licence+Internet/default.aspx', 'keep record of derivative products', 'http://www.environment.gov.au/coasts/mbp/imcra/index.html');
